mapper.select:
select

from ums_admin
where username = #{username}
and password = #{password}

mapper.insert:
insert into ums_admin (username, `password`, icon, 
  email, nick_name, note, 
  create_time, login_time, `status`
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{icon,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{nick_name,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}, 
  #{create_time,jdbcType=TIMESTAMP}, #{login_time,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}
  )
